// Simple Intent Detection (No AI needed for testing)
const request = ($json.body?.request || $json.request || '').toLowerCase();
const customerPhone = $json.body?.customerPhone || $json.customerPhone || 'unknown';
const customerName = $json.body?.customerName || $json.customerName || 'Customer';

let intent = 'general_inquiry';
let response = '';

// Detect intent based on keywords
if (request.includes('appointment') && (request.includes('what') || request.includes('my') || request.includes('have'))) {
  intent = 'get_appointments';
  response = `You have 2 upcoming appointments: 1. Oil change on Friday, November 1st at 2:00 PM. 2. Tire rotation on Monday, November 4th at 10:00 AM.`;

} else if (request.includes('book') || request.includes('schedule') || request.includes('make appointment')) {
  intent = 'book_appointment';
  response = `Perfect! I've scheduled your appointment. You'll receive a confirmation shortly.`;

} else if (request.includes('available') || request.includes('availability') || request.includes('what times')) {
  intent = 'check_availability';
  response = `We have the following times available: 9:00 AM, 11:30 AM, 2:00 PM, and 4:30 PM. Which time works best for you?`;

} else if (request.includes('status') || request.includes('ready') || request.includes('service')) {
  intent = 'service_status';
  response = `Your vehicle is currently being serviced. We're about 75% complete and your car should be ready by 3:00 PM today.`;

} else {
  intent = 'general_inquiry';
  response = `I can help you with booking appointments, checking your existing appointments, viewing available times, or checking on your vehicle's service status. What would you like to do?`;
}

return {
  success: true,
  intent: intent,
  message: response,
  customerPhone: customerPhone,
  customerName: customerName,
  request: request,
  timestamp: new Date().toISOString()
};
